// Serialization example in C#\n    var xmlSerializer = new XmlSerializer(typeof(MyData));\n    using (var stringWriter = new StringWriter())\n    {\n        xmlSerializer.Serialize(stringWriter, myDataInstance);\n        string xmlString = stringWriter.ToString();\n    }\n\n    // Deserialization example in C#\n    using (var stringReader = new StringReader(xmlString))\n    {\n        var myDataInstance = (MyData)xmlSerializer.Deserialize(stringReader);\n    }