private void updateRecord(TableName updatedRecord)\n{\n    using (context db = new context())\n    {\n        // Retrieve the existing record from the database\n        var existingRecord = db.TableName.SingleOrDefault(r => r.PrimaryKey == updatedRecord.PrimaryKey);\n        \n        if (existingRecord != null)\n        {\n            // Copy the timestamp and any other properties that shouldn't change\n            updatedRecord.TimeStamp = existingRecord.TimeStamp;\n\n            // Attach the updated record\n            db.TableName.Attach(updatedRecord, true);\n            \n            // Submit changes\n            db.SubmitChanges();\n        }\n        else\n        {\n            throw new InvalidOperationException("Record not found.");\n        }\n    }\n}