using System;\nusing System.Net.Http;\nusing System.Net.Http.Headers;\nusing System.Threading.Tasks;\n\npublic class TwitterClient\n{\n    private static readonly string ConsumerKey = "your_consumer_key";\n    private static readonly string ConsumerSecret = "your_consumer_secret";\n    private static readonly string AccessToken = "your_access_token";\n    private static readonly string AccessTokenSecret = "your_access_token_secret";\n\n    public static async Task PostTweet(string status)\n    {\n        var oauth = new OAuthBase();\n        string normalizedUrl, normalizedRequestParameters;\n        string authHeader = oauth.GenerateOAuthHeader(\n            "https://api.twitter.com/1.1/statuses/update.json",\n            ConsumerKey,\n            ConsumerSecret,\n            AccessToken,\n            AccessTokenSecret,\n            "POST",\n            out normalizedUrl,\n            out normalizedRequestParameters\n        );\n\n        using (var client = new HttpClient())\n        {\n            client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("OAuth", authHeader);\n            var content = new FormUrlEncodedContent(new[] { new KeyValuePair<string, string>("status", status) });\n            var response = await client.PostAsync(normalizedUrl, content);\n            var responseBody = await response.Content.ReadAsStringAsync();\n\n            if (response.IsSuccessStatusCode)\n            {\n                Console.WriteLine("Tweet posted successfully.");\n            }\n            else\n            {\n                Console.WriteLine($"Failed to post tweet. Status code: {response.StatusCode}, Response: {responseBody}");\n            }\n        }\n    }\n}