protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Assuming 'MyTable' is the ID of your HTML table control\n        HideColumns(MyTable);\n    }\n}\n\nprivate void HideColumns(HtmlTable table)\n{\n    foreach (HtmlTableRow row in table.Rows)\n    {\n        if (row.Cells.Count >= 4)\n        {\n            row.Cells[row.Cells.Count - 1].Visible = false; // Hide the last column\n            row.Cells[row.Cells.Count - 2].Visible = false; // Hide the second last column\n        }\n    }\n}