using System;\nusing System.Runtime.InteropServices;\n\n[StructLayout(LayoutKind.Explicit)]\nstruct ExplicitStruct\n{\n    [FieldOffset(0)]\n    public int intValue;\n\n    [FieldOffset(0)]\n    public float floatValue;\n}\n\nclass Program\n{\n    static void Main()\n    {\n        ExplicitStruct es = new ExplicitStruct();\n        es.intValue = 42;\n        Console.WriteLine(es.floatValue); // This might not produce meaningful output\n    }\n}