using System;\nusing System.Collections.Generic;\n\npublic class Car\n{\n    public string Make;\n    public string Model;\n    public int Year;\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<Car> carList = new List<Car>();\n\n        // Add some cars to the list as examples\n        carList.Add(new Car { Make = "Toyota", Model = "Corolla", Year = 1985 });\n        carList.Add(new Car { Make = "Honda", Model = "Civic", Year = 1995 });\n        carList.Add(new Car { Make = "Ford", Model = "Mustang", Year = 1988 });\n\n        Car myCar;\n\n        // Find the first car made between 1980 and 2000\n        for (int x = 1980; x < 2000; x++)\n        {\n            myCar = carList.Find(byYear(x));\n            if (myCar != null)\n            {\n                Console.WriteLine(myCar.Make + " " + myCar.Model);\n            }\n        }\n    }\n\n    public static Predicate<Car> byYear(int year)\n    {\n        return delegate (Car car)\n        {\n            return car.Year == year;\n        };\n    }\n}