public bool Update(Foo foo)\n   {\n       string query = "UPDATE FooTable SET Name = @Name, Date = @Date WHERE Id = @Id";\n\n       using (SqlConnection conn = new SqlConnection("your_connection_string"))\n       {\n           using (SqlCommand cmd = new SqlCommand(query, conn))\n           {\n               cmd.Parameters.AddWithValue("@Id", foo.Id);\n               cmd.Parameters.AddWithValue("@Name", foo.Name);\n               cmd.Parameters.AddWithValue("@Date", foo.Date);\n\n               conn.Open();\n               int result = cmd.ExecuteNonQuery();\n\n               return result > 0;\n           }\n       }\n   }