public ActionResult Open(string sort, string filterType, string filterValue)\n{\n    var issuesQuery = db.Issues.AsQueryable();\n\n    // Apply filter if provided\n    if (!string.IsNullOrEmpty(filterType) && !string.IsNullOrEmpty(filterValue))\n    {\n        switch (filterType)\n        {\n            case "Technician":\n                issuesQuery = issuesQuery.Where(i => i.Technician == filterValue);\n                break;\n            case "Customer":\n                issuesQuery = issuesQuery.Where(i => i.Customer == filterValue);\n                break;\n            case "Category":\n                issuesQuery = issuesQuery.Where(i => i.Category == filterValue);\n                break;\n            case "Priority":\n                issuesQuery = issuesQuery.Where(i => i.Priority == filterValue);\n                break;\n            case "Status":\n                issuesQuery = issuesQuery.Where(i => i.Status == filterValue);\n                break;\n            default:\n                break;\n        }\n    }\n\n    // Apply sorting\n    switch (sort)\n    {\n        case "ID":\n            issuesQuery = issuesQuery.OrderBy(i => i.ID);\n            break;\n        case "TimeLogged":\n            issuesQuery = issuesQuery.OrderBy(i => i.TimeLogged);\n            break;\n        case "Technician":\n            issuesQuery = issuesQuery.OrderBy(i => i.Technician);\n            break;\n        case "Customer":\n            issuesQuery = issuesQuery.OrderBy(i => i.Customer);\n            break;\n        case "Category":\n            issuesQuery = issuesQuery.OrderBy(i => i.Category);\n            break;\n        case "Priority":\n            issuesQuery = issuesQuery.OrderBy(i => i.Priority);\n            break;\n        case "Status":\n            issuesQuery = issuesQuery.OrderBy(i => i.Status);\n            break;\n        default:\n            issuesQuery = issuesQuery.OrderBy(i => i.TimeLogged); // Default sorting\n            break;\n    }\n\n    var issues = issuesQuery.ToList();\n\n    ViewData["Title"] = "Open Issues";\n    ViewData["SortID"] = sort.ToString();\n\n    return View(issues);\n}