#nullable enable\n\npublic class NonNullable<T> where T : class\n{\n    private readonly T value;\n\n    public NonNullable(T value)\n    {\n        this.value = value ?? throw new ArgumentNullException(nameof(value));\n    }\n\n    public static implicit operator T(NonNullable<T> nonNullable)\n    {\n        return nonNullable.value;\n    }\n}\n\npublic void TestMethod(NonNullable<string> nonNullableString)\n{\n    string s = nonNullableString; // Safe to use, guaranteed to be non-null\n}\n\n#nullable disable