using VideoGrabber;\n\npublic class VideoCapture\n{\n    private VideoGrabber.VideoGrabber _videoGrabber;\n\n    public VideoCapture()\n    {\n        _videoGrabber = new VideoGrabber.VideoGrabber();\n        SetupVideoGrabber();\n    }\n\n    private void SetupVideoGrabber()\n    {\n        // Set the video and audio codec\n        _videoGrabber.VideoCompressor = CompressorType.ct_WMV;\n        _videoGrabber.AudioCompressor = CompressorType.ct_WMA;\n\n        // Configure other settings as needed\n        _videoGrabber.VideoDevice = 0; // Assuming the first video device\n        _videoGrabber.AudioDevice = 0; // Assuming the first audio device\n        _videoGrabber.RecordingFileName = "output.wmv";\n\n        // Start preview\n        _videoGrabber.StartPreview();\n    }\n\n    public void StartRecording()\n    {\n        _videoGrabber.StartRecording();\n    }\n\n    public void StopRecording()\n    {\n        _videoGrabber.StopRecording();\n    }\n}