Excel.Application xlApp = new Excel.Application();\nxlApp.Visible = false;\nxlApp.ScreenUpdating = false;\nxlApp.DisplayAlerts = false;\nExcel.Worksheet worksheet;\n\n// Assuming worksheet is already initialized\nstring filename = "path_to_your_image_file.eps";\n\n// Insert the image using OleObjects\nExcel.OLEObject oleObject = worksheet.OLEObjects().Add(\n    ClassType: "Paint.Picture",\n    FileName: filename,\n    Link: false,\n    DisplayAsIcon: false,\n    IconFileName: Type.Missing,\n    IconIndex: Type.Missing,\n    IconLabel: Type.Missing,\n    Left: 10, // Set your desired position\n    Top: 10,  // Set your desired position\n    Width: 100, // Set your desired size\n    Height: 100 // Set your desired size\n);\n\nxlApp.ScreenUpdating = true;\nxlApp.Visible = true;