using System;\nusing System.Collections.Generic;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string drive = @"C:\"; // Set the drive you want to search\n        string[] fileExtensions = { ".xml", ".csv", ".xls" }; // File extensions to search for\n\n        List<string> foundFiles = new List<string>();\n\n        foreach (string extension in fileExtensions)\n        {\n            foundFiles.AddRange(FindFiles(drive, extension));\n        }\n\n        foreach (string file in foundFiles)\n        {\n            Console.WriteLine(file);\n        }\n    }\n\n    static List<string> FindFiles(string rootFolder, string extension)\n    {\n        List<string> filesFound = new List<string>();\n\n        try\n        {\n            foreach (string file in Directory.GetFiles(rootFolder, "*" + extension))\n            {\n                filesFound.Add(file);\n            }\n\n            foreach (string directory in Directory.GetDirectories(rootFolder))\n            {\n                filesFound.AddRange(FindFiles(directory, extension));\n            }\n        }\n        catch (UnauthorizedAccessException)\n        {\n            // Handle access denied errors\n            Console.WriteLine($"Access denied to folder: {rootFolder}");\n        }\n        catch (PathTooLongException)\n        {\n            // Handle path too long errors\n            Console.WriteLine($"Path too long: {rootFolder}");\n        }\n        catch (Exception ex)\n        {\n            // Handle other exceptions\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n\n        return filesFound;\n    }\n}