using System;\nusing System.IO;\n\nclass Program\n{\n    public static void Main()\n    {\n        string filePath = @"path\to\your\file.txt";\n        bool isLocked = IsFileLocked(filePath);\n\n        if (isLocked)\n        {\n            Console.WriteLine("The file is currently locked by another process.");\n        }\n        else\n        {\n            Console.WriteLine("The file is not locked.");\n        }\n    }\n\n    private static bool IsFileLocked(string filePath)\n    {\n        try\n        {\n            using (FileStream stream = new FileStream(filePath, FileMode.Open, FileAccess.ReadWrite, FileShare.None))\n            {\n                stream.Close();\n            }\n        }\n        catch (IOException)\n        {\n            return true;\n        }\n        return false;\n    }\n}