public class Service : IService\n    {\n        public ResponseData GetData(string id, string format)\n        {\n            var data = new ResponseData { Id = id, Value = "Sample Data" };\n\n            if (format.ToLower() == "json")\n            {\n                WebOperationContext.Current.OutgoingResponse.Format = WebMessageFormat.Json;\n            }\n            else if (format.ToLower() == "xml")\n            {\n                WebOperationContext.Current.OutgoingResponse.Format = WebMessageFormat.Xml;\n            }\n            else\n            {\n                throw new WebFaultException<string>("Invalid format", HttpStatusCode.BadRequest);\n            }\n\n            return data;\n        }\n    }