// Library1.cs\n    using System;\n    using System.Configuration;\n    using System.IO;\n    using System.Reflection;\n\n    public class Library1\n    {\n        public void ReadSettings()\n        {\n            string configFile = Path.Combine(Path.GetDirectoryName(Assembly.GetExecutingAssembly().Location), "Library1.dll.config");\n            var configMap = new ExeConfigurationFileMap { ExeConfigFilename = configFile };\n            var config = ConfigurationManager.OpenMappedExeConfiguration(configMap, ConfigurationUserLevel.None);\n            string setting1 = config.AppSettings.Settings["Setting1"].Value;\n            // Use setting1\n        }\n    }\n\n    // Library2.cs\n    using System;\n    using System.Configuration;\n    using System.IO;\n    using System.Reflection;\n\n    public class Library2\n    {\n        public void ReadSettings()\n        {\n            string configFile = Path.Combine(Path.GetDirectoryName(Assembly.GetExecutingAssembly().Location), "Library2.dll.config");\n            var configMap = new ExeConfigurationFileMap { ExeConfigFilename = configFile };\n            var config = ConfigurationManager.OpenMappedExeConfiguration(configMap, ConfigurationUserLevel.None);\n            string setting1 = config.AppSettings.Settings["Setting1"].Value;\n            // Use setting1\n        }\n    }