[HttpPost]\npublic ActionResult GetSimilarIssues(string title)\n{\n    var issues = _db.GetSimilarIssues(title).ToList();\n\n    if (string.IsNullOrEmpty(title) || issues.Count == 0)\n    {\n        return Json(new { result = "NO DATA" });\n    }\n\n    var retVal = new StringBuilder();\n    foreach (var issue in issues)\n    {\n        retVal.Append("<div class='answer-summary' style='width: 610px;'>");\n        retVal.AppendFormat("<a href='/Issue.aspx?projid={0}&issuetypeid={1}&issueid={2}'>", issue.ProjectId, issue.IssueTypeId, issue.IssueId);\n        retVal.Append(issue.Summary);\n        retVal.Append("</a>");\n        retVal.Append("</div>");\n    }\n\n    return Json(new { result = retVal.ToString() });\n}