[TestClass]\n   public class HomeControllerTests\n   {\n       [TestMethod]\n       public void Index_Returns_View_With_Correct_Data()\n       {\n           // Arrange\n           var mockCacheProvider = new Mock<ICacheProvider>();\n           mockCacheProvider.Setup(m => m.Get(It.IsAny<string>())).Returns("mock data");\n\n           var controller = new HomeController(mockCacheProvider.Object);\n\n           // Act\n           var result = controller.Index() as ViewResult;\n\n           // Assert\n           Assert.IsNotNull(result);\n           Assert.AreEqual("mock data", result.ViewBag.Data);\n       }\n   }