using System;\nusing System.Data;\nusing System.Web.UI;\nusing Oracle.ManagedDataAccess.Client;\n\npublic partial class YourPage : Page\n{\n    protected void btnLoadData_Click(object sender, EventArgs e)\n    {\n        string connectionString = "your_connection_string_here";\n        using (OracleConnection con = new OracleConnection(connectionString))\n        {\n            using (OracleCommand cmd = new OracleCommand("your_stored_procedure", con))\n            {\n                cmd.CommandType = CommandType.StoredProcedure;\n                cmd.Parameters.Add("your_refcursor_parameter", OracleDbType.RefCursor).Direction = ParameterDirection.Output;\n\n                con.Open();\n                using (OracleDataAdapter da = new OracleDataAdapter(cmd))\n                {\n                    DataTable dt = new DataTable();\n                    da.Fill(dt);\n                    GridView1.DataSource = dt;\n                    GridView1.DataBind();\n                }\n            }\n        }\n    }\n}