using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create the original DataTable and define its schema\n        DataTable originalTable = new DataTable();\n        originalTable.Columns.Add("ID", typeof(int));\n        originalTable.Columns.Add("Name", typeof(string));\n        originalTable.Columns.Add("Age", typeof(int));\n\n        // Use the Clone method to create a new DataTable with the same schema\n        DataTable newTable = originalTable.Clone();\n\n        // Display the schema of the new DataTable\n        foreach (DataColumn column in newTable.Columns)\n        {\n            Console.WriteLine($"Column Name: {column.ColumnName}, Data Type: {column.DataType}");\n        }\n    }\n}