public class DataReaderHelper\n{\n    public static T GetValue<T>(IDataReader reader, string columnName)\n    {\n        int ordinal = reader.GetOrdinal(columnName);\n\n        if (reader.IsDBNull(ordinal))\n        {\n            // Return default value for value types or null for reference types\n            return default(T);\n        }\n\n        // Assuming the reader contains the right type\n        return (T)reader.GetValue(ordinal);\n    }\n}\n\n// Usage example\nusing (var reader = command.ExecuteReader())\n{\n    while (reader.Read())\n    {\n        string name = DataReaderHelper.GetValue<string>(reader, "Name");\n        int age = DataReaderHelper.GetValue<int>(reader, "Age");\n        // Handle other fields accordingly\n    }\n}