using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            string zoneName = "yourZoneName"; // specify your DNS zone name here\n            uint zoneType = 1; // 1 for Primary zone type\n            string dnsServer = "yourDnsServer"; // specify your DNS server name or IP here\n\n            ManagementScope scope = new ManagementScope($@"\\{dnsServer}\root\MicrosoftDNS");\n            ObjectQuery query = new ObjectQuery($"SELECT * FROM MicrosoftDNS_Zone WHERE Name = '{zoneName}'");\n            ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n\n            foreach (ManagementObject zone in searcher.Get())\n            {\n                ManagementBaseObject inParams = zone.GetMethodParameters("ChangeZoneType");\n                inParams["ZoneType"] = zoneType;\n                \n                ManagementBaseObject outParams = zone.InvokeMethod("ChangeZoneType", inParams, null);\n\n                if (outParams != null && outParams["ReturnValue"] != null && (uint)outParams["ReturnValue"] == 0)\n                {\n                    Console.WriteLine("Zone type changed successfully.");\n                }\n                else\n                {\n                    Console.WriteLine("Failed to change zone type.");\n                }\n            }\n        }\n        catch (ManagementException e)\n        {\n            Console.WriteLine("An error occurred: " + e.Message);\n        }\n    }\n}