using System;\nusing System.IO;\nusing System.Linq;\nusing System.Web.UI.WebControls;\n\npublic partial class TaskScheduler : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            LoadExecutables();\n        }\n    }\n\n    private void LoadExecutables()\n    {\n        string directoryPath = @"C:\Path\To\Executables"; // Change this to your directory path\n        var files = Directory.GetFiles(directoryPath, "*.exe");\n\n        ddlExecutables.DataSource = files.Select(Path.GetFileName).ToList();\n        ddlExecutables.DataBind();\n    }\n\n    protected void btnSubmit_Click(object sender, EventArgs e)\n    {\n        string selectedFile = ddlExecutables.SelectedValue;\n        // Pass the selected file path to your .NET wrapper for scheduled tasks\n        // Example: ScheduleTaskWrapper.ScheduleTask(selectedFile);\n    }\n}