using System;\n   using Oracle.ManagedDataAccess.Client; // Make sure to install Oracle.ManagedDataAccess via NuGet\n\n   class Program\n   {\n       static void Main()\n       {\n           string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n\n           using (OracleConnection conn = new OracleConnection(connectionString))\n           {\n               conn.Open();\n\n               using (OracleCommand cmd = new OracleCommand("SELECT * FROM employees", conn))\n               {\n                   OracleDataReader reader = cmd.ExecuteReader();\n                   while (reader.Read())\n                   {\n                       Console.WriteLine(reader.GetString(1)); // Example of reading data\n                   }\n               }\n           }\n       }\n   }