using System;\nusing System.IO;\n\nclass Program\n{\n    private static long lastReadPosition = 0;\n    private static readonly string filePath = "path_to_your_file.csv";\n    \n    static void Main()\n    {\n        FileSystemWatcher watcher = new FileSystemWatcher();\n        watcher.Path = Path.GetDirectoryName(filePath);\n        watcher.Filter = Path.GetFileName(filePath);\n        watcher.NotifyFilter = NotifyFilters.LastWrite;\n\n        watcher.Changed += OnChanged;\n\n        watcher.EnableRaisingEvents = true;\n\n        Console.WriteLine("Monitoring changes to the file. Press 'q' to quit.");\n        while (Console.Read() != 'q') ;\n    }\n\n    private static void OnChanged(object source, FileSystemEventArgs e)\n    {\n        try\n        {\n            using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read, FileShare.ReadWrite))\n            {\n                fs.Seek(lastReadPosition, SeekOrigin.Begin);\n                using (StreamReader reader = new StreamReader(fs))\n                {\n                    string newLine;\n                    while ((newLine = reader.ReadLine()) != null)\n                    {\n                        // Process the new line here (e.g., insert into the database)\n                        Console.WriteLine("New Line: " + newLine);\n                    }\n                    lastReadPosition = fs.Position;\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error reading file: {ex.Message}");\n        }\n    }\n}