// Enum to define the version of the mobile application\npublic enum MobileAppVersion\n{\n    V1,\n    V2\n}\n\n// Base class for mobile data models\npublic class MobileDataModel\n{\n    public MobileAppVersion Version { get; set; }\n}\n\n// v1 of the mobile data model\npublic class MobileDataModelV1 : MobileDataModel\n{\n    public int OrderId { get; set; }\n    public string CustomerName { get; set; }\n}\n\n// v2 of the mobile data model\npublic class MobileDataModelV2 : MobileDataModel\n{\n    public int OrderId { get; set; }\n    public string CustomerName { get; set; }\n    public DateTime OrderDate { get; set; }\n}\n\n// Main application data model\npublic class MainAppDataModel\n{\n    public int OrderId { get; set; }\n    public string CustomerName { get; set; }\n    public DateTime? OrderDate { get; set; }\n}\n\n// Synchronization logic\npublic class DataSynchronizer\n{\n    public void Synchronize(MobileDataModel mobileData)\n    {\n        MainAppDataModel mainData = new MainAppDataModel();\n        \n        switch (mobileData.Version)\n        {\n            case MobileAppVersion.V1:\n                var v1Data = (MobileDataModelV1)mobileData;\n                mainData.OrderId = v1Data.OrderId;\n                mainData.CustomerName = v1Data.CustomerName;\n                // OrderDate is not present in v1, so it is ignored\n                break;\n                \n            case MobileAppVersion.V2:\n                var v2Data = (MobileDataModelV2)mobileData;\n                mainData.OrderId = v2Data.OrderId;\n                mainData.CustomerName = v2Data.CustomerName;\n                mainData.OrderDate = v2Data.OrderDate;\n                break;\n                \n            default:\n                throw new NotSupportedException("Unsupported mobile data version");\n        }\n        \n        // Save mainData to the main application database\n        SaveToMainAppDatabase(mainData);\n    }\n\n    private void SaveToMainAppDatabase(MainAppDataModel mainData)\n    {\n        // Implement saving logic here\n    }\n}