using System;\nusing System.Net.Http;\nusing System.Text;\nusing System.Threading.Tasks;\n\npublic class DataUploader\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    public async Task UploadDataAsync(string apiKey, string jsonData)\n    {\n        try\n        {\n            client.DefaultRequestHeaders.Add("Api-Key", apiKey);\n\n            var content = new StringContent(jsonData, Encoding.UTF8, "application/json");\n            var response = await client.PostAsync("https://your_server.com/api.php", content);\n\n            if (response.IsSuccessStatusCode)\n            {\n                Console.WriteLine("Data successfully uploaded");\n            }\n            else\n            {\n                Console.WriteLine($"Failed to upload data: {response.StatusCode}");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}