using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\nusing System.Web.UI;\n\npublic partial class NewForm : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n    }\n\n    protected async void SubmitButton_Click(object sender, EventArgs e)\n    {\n        string legacyUrl = "https://legacywebsite.com/submit";\n\n        // Collect the form data\n        var formData = new MultipartFormDataContent();\n        formData.Add(new StringContent(Request.Form["field1"]), "field1");\n        formData.Add(new StringContent(Request.Form["field2"]), "field2");\n        // Add more fields as necessary\n\n        using (HttpClient client = new HttpClient())\n        {\n            HttpResponseMessage response = await client.PostAsync(legacyUrl, formData);\n            if (response.IsSuccessStatusCode)\n            {\n                // Handle success\n                Response.Write("Form submitted successfully.");\n            }\n            else\n            {\n                // Handle failure\n                Response.Write("Failed to submit the form.");\n            }\n        }\n    }\n}