using System;\nusing System.ComponentModel.DataAnnotations;\nusing System.Linq;\nusing YourNamespace.Models; // Adjust the namespace to your models\n\npublic class FieldExistsInTablesAttribute : ValidationAttribute\n{\n    protected override ValidationResult IsValid(object value, ValidationContext validationContext)\n    {\n        if (value == null || string.IsNullOrEmpty(value.ToString()))\n        {\n            return ValidationResult.Success;\n        }\n\n        string fieldValue = value.ToString();\n\n        var dbContext = (YourDbContext)validationContext.GetService(typeof(YourDbContext));\n\n        bool existsInTable1 = dbContext.Table1.Any(t => t.FieldName == fieldValue);\n        bool existsInTable2 = dbContext.Table2.Any(t => t.FieldName == fieldValue);\n\n        if (existsInTable1 && existsInTable2)\n        {\n            return ValidationResult.Success;\n        }\n\n        return new ValidationResult($"The field '{fieldValue}' does not exist in both tables.");\n    }\n}