using System;\nusing System.Text.RegularExpressions;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string input = "Statements1  start Statements2 ; Statements3 end fun;";\n        string pattern = @"(.*?)(\bstart\b)([^;]*;.*?\bend\b.*)";\n\n        Match match = Regex.Match(input, pattern);\n        if (match.Success)\n        {\n            Console.WriteLine("Group 1: " + match.Groups[1].Value.Trim());\n            Console.WriteLine("Group 2: " + match.Groups[2].Value.Trim());\n            Console.WriteLine("Group 3: " + match.Groups[3].Value.Trim());\n        }\n    }\n}