public partial class MainWindow : Window\n{\n    public MainWindow()\n    {\n        InitializeComponent();\n\n        MyUserControl Cntrl = new MyUserControl();\n        \n        // Bind TitleWidth to the ActualWidth of the proxy element\n        BindingOperations.SetBinding(Cntrl, MyUserControl.TitleWidthProperty, new Binding\n        {\n            ElementName = "TitleSectionProxy",\n            Path = new PropertyPath("ActualWidth"),\n            Mode = BindingMode.OneWay\n        });\n\n        // Add the user control to your grid or another container\n        MyGridName.Children.Add(Cntrl);\n        Grid.SetRow(Cntrl, 0);\n        Grid.SetColumn(Cntrl, 1); // For example, place it in the second column\n    }\n}