using System;\nusing System.IO;\nusing iTextSharp.text.pdf;\n\npublic class PDFPageCounter\n{\n    public static int GetNumberOfPages(string pdfFilePath)\n    {\n        if (!File.Exists(pdfFilePath))\n        {\n            throw new FileNotFoundException("The specified PDF file does not exist.");\n        }\n\n        using (PdfReader reader = new PdfReader(pdfFilePath))\n        {\n            return reader.NumberOfPages;\n        }\n    }\n\n    public static void Main()\n    {\n        string pdfFilePath = @"path\to\your\pdf\file.pdf";\n        try\n        {\n            int pageCount = GetNumberOfPages(pdfFilePath);\n            Console.WriteLine("The PDF file has {0} pages.", pageCount);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}