using System;\nusing System.Diagnostics;\nusing System.Reflection;\n\npublic class Program\n{\n    public static void Main(string[] args)\n    {\n        string assemblyPath = "path/to/your/assembly.dll"; // Replace with the path to your DLL\n        Assembly assembly = Assembly.LoadFrom(assemblyPath);\n        bool isDebug = IsAssemblyDebugBuild(assembly);\n        Console.WriteLine($"Is Debug Build: {isDebug}");\n    }\n\n    private static bool IsAssemblyDebugBuild(Assembly assembly)\n    {\n        foreach (var attribute in assembly.GetCustomAttributes(false))\n        {\n            if (attribute is DebuggableAttribute debuggableAttribute)\n            {\n                return debuggableAttribute.IsJITTrackingEnabled;\n            }\n        }\n        return false;\n    }\n}