protected void gvResults_SelectedIndexChanged(object sender, EventArgs e)\n{\n    // Trigger the hidden button click to initiate the file download\n    ScriptManager.RegisterStartupScript(this, GetType(), "triggerDownload", "triggerFileDownload();", true);\n}\n\nprotected void btnDownloadReport_Click(object sender, EventArgs e)\n{\n    // Generate and send the report to the client\n    RMATagsReport rpt = new RMATagsReport();\n    byte[] reportData = rpt.GenerateReport();\n\n    Response.Clear();\n    Response.ContentType = "application/pdf"; // or any other appropriate MIME type\n    Response.AddHeader("Content-Disposition", "attachment; filename=RMAReport.pdf");\n    Response.BinaryWrite(reportData);\n    Response.End();\n}