using System;\nusing System.Net;\nusing Microsoft.Web.Services3;\n\npublic class MyWebServiceClient\n{\n    public void CallWebService()\n    {\n        MyWebService myService = new MyWebService();\n\n        // Set the proxy settings\n        WebProxy proxy = new WebProxy("http://theproxy:8080", true);\n        proxy.Credentials = new NetworkCredential("proxyUsername", "proxyPassword"); // if authentication is needed\n\n        myService.Proxy = proxy;\n\n        // Call the web service method\n        myService.MyWebServiceMethod();\n    }\n}