using System.ServiceModel;\n\n[ServiceContract]\npublic interface IMyService\n{\n    [OperationContract]\n    string MyServiceMethod();\n}\n\npublic class MyService : IMyService\n{\n    public string MyServiceMethod()\n    {\n        string feedback = "Operation completed successfully.";\n        // Log the feedback to the server console\n        System.Console.WriteLine(feedback);\n        // Return the feedback to the client\n        return feedback;\n    }\n}