// In your aspx.cs file\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        string promo = Request.QueryString["promo"];\n        if (!string.IsNullOrEmpty(promo))\n        {\n            // Fetch data from the database based on the promo parameter\n            string promoData = GetPromoData(promo);\n            // Send the data to the client-side\n            ClientScript.RegisterStartupScript(this.GetType(), "ShowPromoPopup", "showPromoPopup('" + promoData + "');", true);\n        }\n    }\n}\n\nprivate string GetPromoData(string promo)\n{\n    // Example function to get data from the database\n    // You should replace this with your actual database access code\n    return "Special promotion details for: " + promo;\n}