using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        using (OracleConnection conn = new OracleConnection(connectionString))\n        {\n            conn.Open();\n\n            using (OracleCommand cmd = new OracleCommand("EXECUTE_UPDATE", conn))\n            {\n                cmd.CommandType = CommandType.StoredProcedure;\n\n                // Create and set the id_list parameter\n                OracleParameter idListParam = new OracleParameter();\n                idListParam.ParameterName = "id_list";\n                idListParam.OracleDbType = OracleDbType.Decimal;\n                idListParam.CollectionType = OracleCollectionType.PLSQLAssociativeArray;\n                idListParam.Size = 3; // Adjust the size based on the number of IDs you have\n                idListParam.Value = new decimal[] { 1, 2, 3 }; // Your list of IDs\n                cmd.Parameters.Add(idListParam);\n\n                // Create and set the value parameter\n                OracleParameter valueParam = new OracleParameter("value", OracleDbType.Decimal);\n                valueParam.Value = 10; // The value you want to subtract\n                cmd.Parameters.Add(valueParam);\n\n                // Create the return parameter\n                OracleParameter returnParam = new OracleParameter("return_value", OracleDbType.Decimal);\n                returnParam.Direction = ParameterDirection.ReturnValue;\n                cmd.Parameters.Add(returnParam);\n\n                // Execute the command\n                cmd.ExecuteNonQuery();\n\n                // Retrieve the return value\n                int rowCount = Convert.ToInt32(returnParam.Value);\n                Console.WriteLine($"Rows affected: {rowCount}");\n            }\n        }\n    }\n}