using System;\nusing System.IO;\nusing System.Text;\n\npublic class EncodingConverter\n{\n    public static void ConvertIso8859ToUtf8(string inputFile, string outputFile)\n    {\n        // Read the file using ISO-8859-1 encoding\n        string content;\n        using (StreamReader reader = new StreamReader(inputFile, Encoding.GetEncoding("iso-8859-1")))\n        {\n            content = reader.ReadToEnd();\n        }\n\n        // Write the content to a new file using UTF-8 encoding\n        using (StreamWriter writer = new StreamWriter(outputFile, false, Encoding.UTF8))\n        {\n            writer.Write(content);\n        }\n    }\n\n    public static void Main()\n    {\n        string inputFile = "path/to/your/input.csv";\n        string outputFile = "path/to/your/output.csv";\n        \n        ConvertIso8859ToUtf8(inputFile, outputFile);\n        Console.WriteLine("Conversion successful!");\n    }\n}