using System;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new X509Store object with a custom store name\n        string customStoreName = "MyCustomStore";\n        X509Store store = new X509Store(customStoreName, StoreLocation.LocalMachine);\n\n        try\n        {\n            // Open the store in read/write mode\n            store.Open(OpenFlags.ReadWrite);\n\n            // Add a certificate to the store (for demonstration purposes, we're just creating a self-signed certificate)\n            X509Certificate2 cert = new X509Certificate2("path_to_your_certificate.pfx", "certificate_password");\n            store.Add(cert);\n\n            Console.WriteLine("Certificate added to custom store successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n        finally\n        {\n            // Always close the store\n            store.Close();\n        }\n    }\n}