using (var document = new Document(PageSize.A4))\n   {\n       PdfWriter.GetInstance(document, new FileStream("Canvas.pdf", FileMode.Create));\n       document.Open();\n       foreach (var obj in canvasObjects)\n       {\n           if (obj.Type == "Text")\n           {\n               var paragraph = new Paragraph(obj.Content);\n               paragraph.SetAbsolutePosition((float)obj.X, (float)obj.Y);\n               document.Add(paragraph);\n           }\n           else if (obj.Type == "Image")\n           {\n               var image = Image.GetInstance(new Uri(obj.Content));\n               image.SetAbsolutePosition((float)obj.X, (float)obj.Y);\n               image.ScaleAbsolute((float)obj.Width, (float)obj.Height);\n               document.Add(image);\n           }\n       }\n       document.Close();\n   }