[TestClass]\npublic class OrdersServiceTests\n{\n    [TestMethod]\n    public void GetOrdersByUserID_UserLoggedIn_ReturnsOrders()\n    {\n        // Arrange\n        var mockUserContext = new Mock<IUserContext>();\n        mockUserContext.Setup(uc => uc.LoggedInUser).Returns(new User { ID = 1 });\n\n        var ordersService = new OrdersService(mockUserContext.Object);\n\n        // Act\n        var orders = ordersService.GetOrdersByUserID();\n\n        // Assert\n        Assert.IsNotNull(orders);\n        // Additional assertions as needed\n    }\n}