public class YourApiController : ApiController\n{\n    [HttpPost]\n    [Route("api/endpoint")]\n    public IHttpActionResult YourMethod([FromBody] YourDataModel data)\n    {\n        var apiKey = Request.Headers.Authorization?.Parameter;\n        \n        if (!IsValidApiKey(apiKey))\n        {\n            return Unauthorized();\n        }\n        \n        // Process the data\n        // ...\n        \n        return Ok();\n    }\n\n    private bool IsValidApiKey(string apiKey)\n    {\n        // Implement your API key validation logic here\n        // For example, check against a database or predefined list\n        return apiKey == "YOUR_VALID_API_KEY";\n    }\n}