using Renci.SshNet;\n\npublic void UploadFileToSftp(string sftpHost, string username, string password, string filePath, string remotePath)\n{\n    using (var sftp = new SftpClient(sftpHost, username, password))\n    {\n        sftp.Connect();\n        using (var fileStream = new FileStream(filePath, FileMode.Open))\n        {\n            sftp.UploadFile(fileStream, remotePath);\n        }\n        sftp.Disconnect();\n    }\n}