using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"<root>\n                         <function name='Example'/>\n                         <function name='Test'/>\n                       </root>";\n        XElement doc = XElement.Parse(xml);\n        string searchText = "example".ToLower();\n\n        var results = doc.Descendants("function")\n                         .Where(x => ((string)x.Attribute("name"))\n                         .ToLower().StartsWith(searchText));\n\n        foreach (var result in results)\n        {\n            Console.WriteLine(result);\n        }\n    }\n}