using System;\nusing System.Data.SqlClient;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string";\n        var userTimeZone = TimeZoneInfo.FindSystemTimeZoneById("Eastern Standard Time");\n\n        using (var connection = new SqlConnection(connectionString))\n        {\n            var command = new SqlCommand("SELECT Id, Data, CreatedAt FROM Records", connection);\n            \n            connection.Open();\n            using (var reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    var createdAtUtc = (DateTime)reader["CreatedAt"];\n                    var createdAtLocal = TimeZoneInfo.ConvertTimeFromUtc(createdAtUtc, userTimeZone);\n                    Console.WriteLine($"Id: {reader["Id"]}, Data: {reader["Data"]}, CreatedAt (Local): {createdAtLocal}");\n                }\n            }\n        }\n    }\n}