using System;\nusing System.DirectoryServices.AccountManagement;\n\npublic class UserGroupCheck\n{\n    public static bool IsUserInGroup(string domain, string username, string groupName)\n    {\n        try\n        {\n            using (PrincipalContext pc = new PrincipalContext(ContextType.Domain, domain))\n            {\n                UserPrincipal user = UserPrincipal.FindByIdentity(pc, username);\n                \n                if (user != null)\n                {\n                    GroupPrincipal group = GroupPrincipal.FindByIdentity(pc, groupName);\n                    \n                    if (group != null)\n                    {\n                        return user.IsMemberOf(group);\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n\n        return false;\n    }\n\n    public static void Main(string[] args)\n    {\n        string domain = "YOUR_DOMAIN";\n        string username = "USERNAME";\n        string groupName = "Administrators";\n\n        bool isAdmin = IsUserInGroup(domain, username, groupName);\n\n        Console.WriteLine($"Is user an administrator: {isAdmin}");\n    }\n}