// Assume dataSet, Product, Feature, and Supplier tables are already populated\nDataSet dataSet = new DataSet();\nDataTable productTable = dataSet.Tables["Product"];\nDataTable featureTable = dataSet.Tables["Feature"];\nDataTable supplierTable = dataSet.Tables["Supplier"];\n\n// Create DataRelations to link the tables\nDataRelation productFeatureRelation = new DataRelation("ProductFeature", \n    productTable.Columns["ProductID"], featureTable.Columns["ProductID"]);\nDataRelation productSupplierRelation = new DataRelation("ProductSupplier", \n    productTable.Columns["ProductID"], supplierTable.Columns["ProductID"]);\n\ndataSet.Relations.Add(productFeatureRelation);\ndataSet.Relations.Add(productSupplierRelation);\n\n// Filter Feature and Supplier tables\nDataRow[] matchingFeatures = featureTable.Select("FeatureName = 'Width'");\nDataRow[] matchingSuppliers = supplierTable.Select("SupplierName = 'Microsoft'");\n\n// Get ProductIDs that match the criteria\nHashSet<int> productIds = new HashSet<int>();\n\nforeach (DataRow featureRow in matchingFeatures)\n{\n    DataRow productRow = featureRow.GetParentRow("ProductFeature");\n    if (productRow != null)\n    {\n        productIds.Add((int)productRow["ProductID"]);\n    }\n}\n\nforeach (DataRow supplierRow in matchingSuppliers)\n{\n    DataRow productRow = supplierRow.GetParentRow("ProductSupplier");\n    if (productRow != null && productIds.Contains((int)productRow["ProductID"]))\n    {\n        productIds.Add((int)productRow["ProductID"]);\n    }\n}\n\n// Filter the Product table based on the collected ProductIDs\nDataRow[] filteredProducts = productTable.Select($"ProductID IN ({string.Join(",", productIds)})");\n\n// Display the filtered products\nforeach (DataRow productRow in filteredProducts)\n{\n    Console.WriteLine($"ProductID: {productRow["ProductID"]}, ProductName: {productRow["ProductName"]}");\n}