// Step 1: Create a copy of the DataTable changes\nDataTable changes = dataSet1.Tables["YourTable"].GetChanges();\n\nif (changes != null)\n{\n    try\n    {\n        // Begin the SQL transaction\n        using (SqlTransaction transaction = sqlConnection.BeginTransaction())\n        {\n            // Step 2: Perform the update operation on the copy\n            dataAdapter1.UpdateCommand.Transaction = transaction;\n            dataAdapter1.InsertCommand.Transaction = transaction;\n            dataAdapter1.DeleteCommand.Transaction = transaction;\n\n            dataAdapter1.Update(changes);\n\n            // Commit the transaction if all rows are updated successfully\n            transaction.Commit();\n        }\n    }\n    catch (Exception ex)\n    {\n        // Step 3: If an error occurs, discard the copy\n        // Optionally, log the error or inform the user\n        Console.WriteLine("Update failed: " + ex.Message);\n\n        // No need to call RejectChanges since the original DataTable isn't modified\n    }\n}\nelse\n{\n    Console.WriteLine("No changes to update.");\n}\n\n// Now the original DataTable remains unchanged, and user can correct the error and try again.