using System;\nusing System.Data.SqlClient;\nusing System.Diagnostics;\nusing System.ServiceProcess;\n\npublic class QueueListenerService : ServiceBase\n{\n    private string connectionString = "your_connection_string_here";\n    private string queueName = "YourQueue";\n\n    protected override void OnStart(string[] args)\n    {\n        ListenToQueue();\n    }\n\n    private void ListenToQueue()\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            while (true)\n            {\n                using (SqlCommand command = new SqlCommand($"WAITFOR (RECEIVE * FROM {queueName});", connection))\n                {\n                    using (SqlDataReader reader = command.ExecuteReader())\n                    {\n                        if (reader.Read())\n                        {\n                            string messageBody = reader["message_body_column"].ToString();\n                            ExecuteCommandOnRemoteMachine(messageBody);\n                        }\n                    }\n                }\n            }\n        }\n    }\n\n    private void ExecuteCommandOnRemoteMachine(string commandArgs)\n    {\n        Process.Start("your_executable_path", commandArgs);\n    }\n\n    protected override void OnStop()\n    {\n        // Implement your stop logic here\n    }\n\n    public static void Main()\n    {\n        ServiceBase.Run(new QueueListenerService());\n    }\n}