using System;\nusing System.Web;\nusing System.IO;\n\npublic partial class FileUpload : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n    }\n\n    protected void UploadButton_Click(object sender, EventArgs e)\n    {\n        if (FileUploadControl.HasFile)\n        {\n            try\n            {\n                string filename = Path.GetFileName(FileUploadControl.FileName);\n                string tempPath = Path.GetTempPath();\n                string filePath = Path.Combine(tempPath, filename);\n                \n                // Save the file to the temp directory\n                FileUploadControl.SaveAs(filePath);\n                \n                // Perform virus scan on the file here\n                // Example: VirusScan(filePath);\n\n                // If the file is clean, move it to the desired directory\n                string finalPath = Server.MapPath("~/Uploads/") + filename;\n                File.Move(filePath, finalPath);\n\n                UploadStatusLabel.Text = "File uploaded successfully!";\n            }\n            catch (Exception ex)\n            {\n                UploadStatusLabel.Text = "File upload failed: " + ex.Message;\n            }\n        }\n    }\n}